{
  "version": "Notebook/1.0",
  "items": [
    {
      "type": 3,
      "content": {
        "version": "KqlItem/1.0",
        "query": "AIObservabilityMetrics_CL | extend status_val = coalesce(status, status_s) | summarize errors = countif(status_val == 'error'), total = count() by bin(TimeGenerated, 5m) | extend errorRate = todouble(errors)/todouble(total)",
        "size": 1,
        "queryType": 0,
        "resourceType": "microsoft.operationalinsights/workspaces"
      },
      "name": "Error Rate Trend"
    },
    {
      "type": 3,
      "content": {
        "version": "KqlItem/1.0",
        "query": "AIObservabilityMetrics_CL | extend lat = todouble(coalesce(latency_ms, latency_ms_d)) | summarize avgLatency = avg(lat) by bin(TimeGenerated, 5m)",
        "size": 1,
        "queryType": 0,
        "resourceType": "microsoft.operationalinsights/workspaces"
      },
      "name": "Latency Trend"
    }
  ]
}
